function
str2long($uf, $ah)
{
    $W = array_values(unpack('V*', str_pad($uf, 4 * ceil(strlen($uf) / 4), "\0")));
    if ($ah) $W[] = strlen($uf);
    return $W;
}

function
xxtea_mx($fh, $eh, $Vf, $dd)
{
    return
        int32((($fh >> 5 & 0x7FFFFFF) ^ $eh << 2) + (($eh >> 3 & 0x1FFFFFFF) ^ $fh << 4)) ^ int32(($Vf ^ $eh) + ($dd ^ $fh));
}

function
encrypt_string($Pf, $_)
{
    if ($Pf == "") return "";
    $_ = array_values(unpack("V*", pack("H*", md5($_))));
    $W = str2long($Pf, true);
    $Md = count($W) - 1;
    $fh = $W[$Md];
    $eh = $W[0];
    $H = floor(6 + 52 / ($Md + 1));
    $Vf = 0;
    while ($H-- > 0) {
        $Vf = int32($Vf + 0x9E3779B9);
        $Nb = $Vf >> 2 & 3;
        for ($we = 0; $we < $Md; $we++) {
            $eh = $W[$we + 1];
            $Ld = xxtea_mx($fh, $eh, $Vf, $_[$we & 3 ^ $Nb]);
            $fh = int32($W[$we] + $Ld);
            $W[$we] = $fh;
        }
        $eh = $W[0];
        $Ld = xxtea_mx($fh, $eh, $Vf, $_[$we & 3 ^ $Nb]);
        $fh = int32($W[$Md] + $Ld);
        $W[$Md] = $fh;
    }
    return
        long2str($W, false);
}

function
decrypt_string($Pf, $_)
{
    if ($Pf == "") return "";
    if (!$_) return
        false;
    $_ = array_values(unpack("V*", pack("H*", md5($_))));
    $W = str2long($Pf, false);
    $Md = count($W) - 1;
    $fh = $W[$Md];
    $eh = $W[0];
    $H = floor(6 + 52 / ($Md + 1));
    $Vf = int32($H * 0x9E3779B9);
    while ($Vf) {
        $Nb = $Vf >> 2 & 3;
        for ($we = $Md; $we > 0; $we--) {
            $fh = $W[$we - 1];
            $Ld = xxtea_mx($fh, $eh, $Vf, $_[$we & 3 ^ $Nb]);
            $eh = int32($W[$we] - $Ld);
            $W[$we] = $eh;
        }
        $fh = $W[$Md];
        $Ld = xxtea_mx($fh, $eh, $Vf, $_[$we & 3 ^ $Nb]);
        $eh = int32($W[0] - $Ld);
        $W[0] = $eh;
        $Vf = int32($Vf - 0x9E3779B9);
    }
    return
        long2str($W, true);
}

