}

function
str2long($sf, $Wg)
{
    $W = array_values(unpack('V*', str_pad($sf, 4 * ceil(strlen($sf) / 4), "\0")));
    if ($Wg) $W[] = strlen($sf);
    return $W;
}

function
xxtea_mx($bh, $ah, $Uf, $cd)
{
    return
        int32((($bh >> 5 & 0x7FFFFFF) ^ $ah << 2) + (($ah >> 3 & 0x1FFFFFFF) ^ $bh << 4)) ^ int32(($Uf ^ $ah) + ($cd ^ $bh));
}

function
encrypt_string($Of, $z)
{
    if ($Of == "") return "";
    $z = array_values(unpack("V*", pack("H*", md5($z))));
    $W = str2long($Of, true);
    $Md = count($W) - 1;
    $bh = $W[$Md];
    $ah = $W[0];
    $H = floor(6 + 52 / ($Md + 1));
    $Uf = 0;
    while ($H-- > 0) {
        $Uf = int32($Uf + 0x9E3779B9);
        $Mb = $Uf >> 2 & 3;
        for ($ve = 0; $ve < $Md; $ve++) {
            $ah = $W[$ve + 1];
            $Ld = xxtea_mx($bh, $ah, $Uf, $z[$ve & 3 ^ $Mb]);
            $bh = int32($W[$ve] + $Ld);
            $W[$ve] = $bh;
        }
        $ah = $W[0];
        $Ld = xxtea_mx($bh, $ah, $Uf, $z[$ve & 3 ^ $Mb]);
        $bh = int32($W[$Md] + $Ld);
        $W[$Md] = $bh;
    }
    return
        long2str($W, false);
}

function
decrypt_string($Of, $z)
{
    if ($Of == "") return "";
    if (!$z) return
        false;
    $z = array_values(unpack("V*", pack("H*", md5($z))));
    $W = str2long($Of, false);
    $Md = count($W) - 1;
    $bh = $W[$Md];
    $ah = $W[0];
    $H = floor(6 + 52 / ($Md + 1));
    $Uf = int32($H * 0x9E3779B9);
    while ($Uf) {
        $Mb = $Uf >> 2 & 3;
        for ($ve = $Md; $ve > 0; $ve--) {
            $bh = $W[$ve - 1];
            $Ld = xxtea_mx($bh, $ah, $Uf, $z[$ve & 3 ^ $Mb]);
            $ah = int32($W[$ve] - $Ld);
            $W[$ve] = $ah;
        }
        $bh = $W[$Md];
        $Ld = xxtea_mx($bh, $ah, $Uf, $z[$ve & 3 ^ $Mb]);
        $ah = int32($W[0] - $Ld);
        $W[0] = $ah;
        $Uf = int32($Uf - 0x9E3779B9);
    }
    return
        long2str($W, true);
}

